@namespace url(http://www.w3.org/1999/xhtml);
/*
 * Basic layout
 */

.container {
  width: 100% !important;
  padding-right: 30px !important;
  padding-left: 30px !important;
}

/* Responsive containers on some pages */
.container-lg,
.container-xl {
  max-width: 100% !important;
  padding-right: 30px !important;
  padding-left: 30px !important;
}

/* Match the header to container padding */
.Header {
  padding-right: 30px !important;
  padding-left: 30px !important;
}


/*
 * Profile pages
 */

.pinned-repo-item {
  width: calc(50% - 10px) !important;
}
.org-pinned-repos-list .pinned-repo-item {
  width: calc(33.3% - 10px) !important;
}

.u-photo,
.user-status-container {
  max-width: 250px;
}
.u-photo .avatar {
  width: 100% !important;
  height: auto !important;
}


/*
 * Repo-specific stuff
 */

.repository-content {
  width: 100% !important;
}

/* Undo container changes for the Projects tab on repos which have unnecessary nested container-lg's */
.repository-content > .container-lg {
  padding-right: 0 !important;
  padding-left: 0 !important;
}

/* Undo for profile timeline */
.contribution-activity-listing .discussion-timeline {
  width: 100% !important;
}

/* Fix #18 - props: @auscompgeek */
.file-header::after {
  clear: left !important;
}

/* Network graph */
#network {
  max-width: 730px;
}


/*
 * Issues & Dashboard
 */

#dashboard,
.new-issue-form {
  position: relative !important;
}

.new-issue-form .discussion-sidebar {
  position: absolute !important;
  top: 0 !important;
  right: 0 !important;
}

button.discussion-sidebar-toggle {
  width: 100% !important;
}

.timeline-new-comment {
  max-width: none !important;
}

/* Commits: extended message under "..." */
.commit-desc pre {
  max-width: none;
}

/* Alert messages */
#js-flash-container .flash-messages {
  width: 100% !important;
  padding-left: 30px !important;
  padding-right: 30px !important;
}

:root {
  --dark-text-color: #272822;
  --code-background-color: #282c34;

  --font-family: Roboto;
}

body {
  font-family: val(--font-family);
  font-size: 125%;
  color: var(--text-color);
  background-color: #eee8d5;
}

h1 {
  color: var(--dark-text-color);
}

h2 {
  color: var(--dark-text-color);
}

h3 {
  width: 100%;
  color: var(--dark-text-color);
  text-align: center;
}

h6 {
  width: 100%;
  color: #F9F8F5;
  text-align: center;
}

b {
  color: #F92672;
}

a.nav {
  color: var(--dark-text-color);
}

a.leftNav {
  float: left;
  color: #A6E22E;
}

a.tutLink {
  margin: 0px auto;
}

div.tutPreface {
  width: 90%;
  margin: 30px auto 50px auto;
  padding: 10px 5px 10px 5px;
  border: medium solid #F4BF75;
  background-color: #f9f8f5;
  text-align: left;
}

div.tutCodeHeader {
  width: 90%;

  margin: 0px auto 0px auto;
  padding: 10px;

  background-color: #F9F8F5;
  border: 1px solid black;

  font-weight: bold;
  color: #272822;
  text-align: left;
}

div.tutCode {
  font-family: Jetbrains Mono;
  color: #f9f8f5;

  width: 90%;

  margin: 15px auto;

  padding: 20px;
  background-color: var(--code-background-color);
  white-space: pre;
}

div.tutText {
  width: 90%;
  background-color: #f9f8f5;
  margin: 0px auto 40px auto;

  padding: 20px;
}

div.tutImg {
  margin: 0px auto 0px auto;
  text-align: center;
}

div.tutFooter {
  width: 90%;
  margin: 30px auto 50px auto;
  padding: 10px 5px 10px 5px;
  border: thin solid #111144;
  background-color: #f9f8f5;
  text-align: left;
}

td.ToCTitle {
  background-color: #272822;
  border: thin solid #66D9EF;
  color: #f9f8f5;
  height: 30px;
  text-align: center;
}

td.tutLink {
  background-color: #f9f8f5;
  border: thin solid #FFF;
  color: #272822;
  text-align: center;
}
